#include<iostream>
#include<string>
using namespace std;
string bPlus(string sl,string ss);
string bPlus(string sl,string ss){
       string f;
       int nno=0,tn;
       char c;
       for(int i=0;i<sl.length();i++)
       {
               tn=(i<ss.length())?sl[sl.length()-i-1]+ss[ss.length()-i-1]-96+nno:sl[sl.length()-i-1]-48+nno;
               if(tn>=10){tn-=10;nno=1;}else{nno=0;}
               c=tn+48;
               f=c+f;
       }
       if(nno!=0){c=nno+48; f=c+f;}
       return f;
}
int main(){
    string fib[5001];
    int n;
    fib[0]="0";fib[1]="1";
    for(int i=2;i<1001;i++)fib[i]=bPlus(fib[i-1],fib[i-2]);
    while(scanf("%d",&n)!=EOF)if(n==5000)cout<<"The Fibonacci number for 5000 is "<<endl; else printf("The Fibonacci number for %d is %s\n",n,fib[n].c_str());
    return 0;
}
